import React, { useState } from 'react';
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Homes = ()=> {
    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')
    const [username,setUsername] = useState('')
    const [data,setData] = useState('')
    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
      }, []);

    const submit = async (e)=>{
        e.preventDefault()
        try {
            await axios.post("http://localhost:4000/users/signup",{email,password,username})
            navigate("/")
            alert("New user has been created");
        }catch(e){
            console.log(e);
        }
    }
    
    const fetchData = async () => {
        try {
          const response = await fetch("http://localhost:4000/users/signup"); 
          if (response.ok) {
            const result = await response.json();
            setData(result);
          } else {
            console.error('Failed to fetch data from the server.');
          }
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      };
    

    return (
        <div>
        <form action="POST">
        <input type="text" placeholder="Enter your email" onChange={(e)=>{setEmail(e.target.value)}}></input>
        <input type="password" placeholder="Enter your password" onChange={(e)=>{setPassword(e.target.value)}}></input>
        <input type="text"  placeholder="Enter your username" onChange={(e)=>{setUsername(e.target.value)}}></input>
       <input type="submit" onClick={submit} value="submit" />
       {data ? (
        <p>{data.message}</p>
      ) : (
        <p>Loading data from the server...</p>
      )}
       </form>
        </div>
    )
}

export default Homes;
